  import React, { Component } from "react";
  import {FormControl,Button,Form,Nav,Navbar,Row,Card,Col,Breadcrumb} from 'react-bootstrap';
  import formErrors from '../FormErrors.js'

  class Inscription extends React.Component {

    constructor(props)
    {

      super(props);

      this.state = {

       id_users : "",
       first_name_users : "",
       last_name_users : "",
       email_users : "",
       password_users : "",
       password_users_confirm : "",
       is_admin_users : false,
       formErrors: {id_users : "",first_name_users : "",last_name_users : "",email_users : "",password_users : "",password_users_confirm : "",is_admin_users : false}
     }

     this.handleInput = this.handleInput.bind(this);
     this.onSubmit = this.onSubmit.bind(this);
     this.validateField = this.validateField(this);

   }

  validateField(fieldName, value) {
    let fieldValidationErrors = this.state.formErrors;
    let emailValid = this.state.emailValid;
    let passwordValid = this.state.passwordValid;

    switch(fieldName) {
      case 'email':
        emailValid = value.match(/^([\w.%+-]+)@([\w-]+\.)+([\w]{2,})$/i);
        fieldValidationErrors.email = emailValid ? '' : ' is invalid';
        break;
      case 'password':
        passwordValid = value.length >= 6;
        fieldValidationErrors.password = passwordValid ? '': ' is too short';
        break;
      default:
        break;
    }
    this.setState({formErrors: fieldValidationErrors,
                    emailValid: emailValid,
                    passwordValid: passwordValid
                  }, this.validateForm);
  }

   onSubmit(e) {

    e.preventDefault();

    this.props.history.push('/menu');

  }



  handleInput = (e) => {
   const name = e.target.name;
   const value = e.target.value;
    this.setState({[name]: value},() => { this.validateField(name, value)});
 }


 render() {


  return (
    <div className="container">

    <h2 className="title_used"> Inscription </h2>

    <br/>

    <Row>

    <Col xs={{ span: 10, offset: 1 }}>

    <div class="panel_etablishment">

    <Form onSubmit={this.onSubmit}>

    <Row>

    <Col xs={6}>
    <Form.Group className="label_form">
    <Form.Label className="info_form" column sm={3}>
    First name : 
    </Form.Label>
    <Col sm={9}>
    <Form.Control
    type="text"  
    value={this.state.first_name_users}
    onChange={this.handleInput}
    name="first_name_users"
    />
    </Col>
    </Form.Group>
    </Col>

    <Col xs={6}>
    <Form.Group className="label_form">
    <Form.Label className="info_form" column sm={3}>
    Last name :
    </Form.Label>
    <Col sm={9}>
    <Form.Control  
    type="text" 
    value={this.state.last_name_users}
    onChange={this.handleInput}
    name="last_name_users"/>
    </Col>
    </Form.Group>
    </Col>

    <Col xs={6}>
    <Form.Group className="label_form">
    <Form.Label className="info_form" column sm={3}>
    E-mail :
    </Form.Label>
    <Col sm={9}>
    <Form.Control  
    type="email" 
    value={this.state.email_users}
    onChange={this.handleInput}
    name="email_users"/>
    </Col>
    </Form.Group>
    </Col>

    <Col xs={6}>
    <Form.Group className="label_form">
    <Form.Label className="info_form" column sm={3}>
    Password :
    </Form.Label>
    <Col sm={9}>
    <Form.Control  
    type="password" 
    value={this.state.password_users}
    onChange={this.handleInput}
    name="password_users"/>
    </Col>
    </Form.Group>
    </Col>

    <Col xs={6}>
    <Form.Group className="label_form">
    <Form.Label className="info_form" column sm={3}>
    Confirm password :
    </Form.Label>
    <Col sm={9}>
    <Form.Control  
    type="password" 
    value={this.state.password_users_confirm}
    onChange={this.handleInput}
    name="password_users_confirm"/>
    </Col>
    </Form.Group>
    </Col>


    <Col xs={6}>
    <Form.Check label="admin" aria-label="option 1" />
    </Col>

    <Col xs={{ span: 2, offset: 10 }}>
    <div className="label_form">
    <input className="send_info" type="submit" value="Send"/>
    </div>
    </Col>

    </Row>

    </Form>

    </div>

    </Col>

    </Row>

    </div>)

}

}

export default Inscription;
